library(shiny)
library(shinyMobile)

ui <- f7Page(
  title = "Mi App Shiny Mobile",
  f7SingleLayout(
    navbar = f7Navbar(title = "App Básica"),
    f7Block(
      h1("Bienvenido a mi app Shiny Mobile"),
      f7Button("miBoton", "Haz clic aquí"),
      uiOutput("resultado")
    )
  )
)

server <- function(input, output, session) {
  observeEvent(input$miBoton, {
    output$resultado <- renderUI({
      f7Block("¡Has hecho clic en el botón!")})
      
    
  })
}

shinyApp(ui, server)
